import Input from "../../UI/Input/Input";
import classes from "./MealItemForm.module.css";
import {useContext, useState} from "react";
import CartItems from "../../../store/cart-items";
import DUMMY_MEALS from "../../../store/dummy-meals";

const MealItemForm = (props) => {
    const items = useContext(CartItems);

    const [amount, setAmount] = useState();

    const amountChangeHandler = (event) => {
        event.preventDefault();
        setAmount(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();
        if(items.items.filter((item) => item.id === props.id).length === 0) {
            let item = (({name, price}) =>
                ({name, price, amount: amount}))(DUMMY_MEALS.filter((item) =>
                item.id === props.id)[0])
            items.addItem(item);
            console.log(item);
        }
    }

    return (
      <form className={classes.form} onSubmit={submitHandler}>
          <Input
              label="Amount"
              type="number"
              id={props.id}
              onChange={amountChangeHandler}/>
          <button type="submit">+ Add</button>
      </form>
    );
}

export default MealItemForm;
