import classes from "./OrderForm.module.css";
import useInput from "../../hooks/use-input";
import {useContext} from "react";
import CartItems from "../../store/cart-items";
import Modal from "../UI/Modal/Modal";
import OrderItem from "./OrderItem/OrderItem";


const OrderForm = (props) => {
    const cartContext = useContext(CartItems);
    const orderItems = cartContext.items.map((item) => {
        return (
            <OrderItem
                key={item.id}
                id={item.id}
                price={item.price}
                name={item.name}
                amount={item.amount}
            />
        );
    });

    const {
        value: enteredName,
        isValid: nameIsValid,
        hasError: nameHasError,
        valueChangeHandler: nameChangeHandler,
        inputBlurHandler: nameInputBlurHandler,
        reset: resetNameInput,
    } = useInput((name) => name.trim() !== '');

    const validateEmail = (email) => {
        return String(email)
            .toLowerCase()
            .match(
                /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
            );
    };

    const {
        value: enteredEmail,
        isValid: emailIsValid,
        hasError: emailHasError,
        valueChangeHandler: emailChangeHandler,
        inputBlurHandler: emailInputBlurHandler,
        reset: resetEmailInput,
    } = useInput(validateEmail);

    let formIsValid = false;
    if (nameIsValid && emailIsValid) {
        formIsValid = true;
    }

    const submitHandler = (event) => {
        event.preventDefault();

        props.onClose();

        if (nameHasError || emailHasError) {
            return;
        }

        const order = {
          name: enteredName,
          email: enteredEmail,
          orderItems: cartContext.items,
        };

        console.log(order);

        resetNameInput();
        resetEmailInput();
    }

    return (
        <Modal>
            <form onSubmit={submitHandler}>
                <ul className={classes["cart-items"]}>
                    {orderItems}
                </ul>
                <div className={classes['control-group']}>
                    <div className={`${classes['form-control']} ${nameHasError && classes.invalid}`}>
                        <label htmlFor='name'>Full Name</label>
                        <input
                            type='text'
                            id='name'
                            value={enteredName}
                            onChange={nameChangeHandler}
                            onBlur={nameInputBlurHandler}
                        />
                        {nameHasError && <p className={classes['error-text']}>Full Name must not be empty.</p>}
                    </div>
                </div>
                <div className={`${classes['form-control']} ${emailHasError && classes.invalid}`}>
                    <label htmlFor='email'>E-Mail Address</label>
                    <input
                        type='text'
                        id='email'
                        value={enteredEmail}
                        onChange={emailChangeHandler}
                        onBlur={emailInputBlurHandler}
                    />
                    {emailHasError && <p className={classes['error-text']}>Email is invalid.</p>}
                </div>
                <div className={classes['form-actions']}>
                    <button disabled={!formIsValid}>Submit</button>
                </div>
            </form>
        </Modal>
    );
};

export default OrderForm;
