import React, {useState} from "react";

const CartItems = React.createContext({
    items: [],
    addItem: (item) => {},
    removeItem: (id) => {},
    findItem: () => {}});

export const CartItemsProvider = (props) => {
    const [items, setItems] = useState([]);

    const addItemHandler = (item) => {
        const prevItem = findItemHandler(item.id);
        if(prevItem === null) {
            setItems((prev) => {
                return prev.push(item);
            });
        } else {
            setItems((prev) => {
                const index = prev.indexOf(prevItem);
                prev[index] = (({name, price, amount}) =>
                    ({name, price, amount: amount + prevItem.amount}))(item);
                return prev;
            });
        }
        console.log(items);
    }

    const removeItemHandler = (item) => {
        setItems((prev) => {
            return prev.splice(prev.indexOf(item), 1);
        });
    }

    const findItemHandler = (id) => {
        const array = items.filter((item) => item.id === id);
        if (array.length > 0) {
            return array[0];
        } else {
            return null;
        }
    }

    return <CartItems.Provider value={{
        items: items,
        addItem: addItemHandler,
        removeItem: removeItemHandler,
        findItem: findItemHandler}}>{props.children}</CartItems.Provider>
}

export default CartItems;
