import React, {useReducer} from "react";

const CartItems = React.createContext({
    items: [],
    addItem: (item) => {
    },
    removeItem: (id) => {
    }
});

const cartReducer = (state, action) => {
    if (action.type === "ADD") {
        const existingCartItemIndex = state.items.findIndex((item) => item.id === action.item.id);
        const prevItem = state.items[existingCartItemIndex];
        let updatedItem;
        let updatedItems;
        if (prevItem) {
            const updatedItem = {
                ...prevItem,
                amount: action.item.amount + prevItem.amount
            }
            updatedItems = [...state.items];
            updatedItems[existingCartItemIndex] = updatedItem;
        } else {
            updatedItems = state.items.concat(action.item);
        }
        return {items: updatedItems};
    }
    if (action.type === "REMOVE") {

    }
    return {items: []}
}

export const CartItemsProvider = (props) => {
    const [cartState, dispatchCartAction] = useReducer(cartReducer, {items: []});

    const addItemHandler = (item) => {
        dispatchCartAction({type: "ADD", item: item});
    }

    const removeItemHandler = (id) => {
        dispatchCartAction({type: "REMOVE", id: id});
        // setItems((prev) => {
        //     return prev.splice(prev.indexOf(item), 1);
        // });
    }

    return <CartItems.Provider value={{
        items: cartState.items,
        addItem: addItemHandler,
        removeItem: removeItemHandler
    }}>{props.children}</CartItems.Provider>
}

export default CartItems;
