import DUMMY_MEALS from "../../../store/dummy-meals";
import MealItem from "../MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import Card from "../../UI/Card/Card";
import useHttp from "../../../hooks/use-http";
import {useCallback, useEffect, useState} from "react";

const AvailableMeals = (props) => {
    const [meals, setMeals] = useState([]);

    const transformMeals = mealsObj => {
        const loadedMeals = [];

        for (const mealKey in mealsObj) {
            loadedMeals.push({
                id: mealKey,
                name: mealsObj[mealKey].name,
                description: mealsObj[mealKey].description,
                price: mealsObj[mealKey].price,
            });
        }

        setMeals(loadedMeals);
    };

    const {
        isLoading,
        error,
        sendRequest,
    } = useHttp();

    useEffect(() => sendRequest({url: "https://react-http-a39f2-default-rtdb.firebaseio.com/meals.json"}, transformMeals), [])

    return (
        <Card className={classes.meals}>
            <ul>
                {DUMMY_MEALS.map((meal) => {
                    return (
                        <MealItem
                            key={meal.id}
                            name={meal.name}
                            description={meal.description}
                            price={meal.price}
                            id={meal.id}/>);
                })}
            </ul>
        </Card>
    );
}

export default AvailableMeals;
