import Header from "./components/Header/Header";
import MealsSummary from "./components/Meals/MealsSummary/MealsSummary";
import AvailableMeals from "./components/Meals/AvailableMeals/AvailableMeals";
import Cart from "./components/Cart/Cart";
import {useState} from "react";
import OrderForm from "./components/OrderForm/OrderForm";

function App() {
    const [isCartOpened, setIsCartOpened] = useState(false);
    const [isOrderOpened, setIsOrderOpened] = useState(false);

    const openCartHandler = () => {
        setIsCartOpened(true);
    }

    const closeCartHandler = () => {
        setIsCartOpened(false);
    }

    const orderHandler = (order) => {
        setIsCartOpened(false);
        setIsOrderOpened(true);
    }

    return (
        <>
            {isCartOpened && <Cart onClose={closeCartHandler} onClick={closeCartHandler} onOrder={orderHandler}/>}
            {isOrderOpened && <OrderForm/>}
            <Header onOpen={openCartHandler}/>
            <main>
                <MealsSummary/>
                <AvailableMeals/>
            </main>
        </>
    );
}

export default App;
