import CartItem from "./CartItem/CartItem";
import {useContext, useState} from "react";
import classes from "./Cart.module.css";
import CartItems from "../../store/cart-items";
import Modal from "../UI/Modal/Modal";
import Checkout from "./Checkout/Checkout";
import useHttp from "../../hooks/use-http";

const Cart = (props) => {
    const [checkoutVisible, setCheckoutVisible] = useState(false);
    const cartContext = useContext(CartItems);
    const {
        isLoading,
        error,
        sendRequest,
    } = useHttp();

    const hasItems = cartContext.items.length > 0;

    const cartItemRemoveHandler = (item) => {
        cartContext.removeItem(item);
    }

    const cartItemAddHandler = (item) => {
        cartContext.addItem({...item, amount: 1});
    }

    let content = <p>Cart is empty :c</p>

    if(hasItems) {
        content = cartContext.items.map((item) => {
            return (
                <CartItem
                    key={item.id}
                    id={item.id}
                    price={item.price}
                    name={item.name}
                    amount={item.amount}
                    onAdd={cartItemAddHandler.bind(null, item)}
                    onRemove={cartItemRemoveHandler.bind(null, item)}/>
            );
        });
    }

    const confirmCheckoutHandler = (data) => {
        data = {
            ...data,
            order: cartContext.items,
        }

        const requestConfig = {
            url: "https://react-http-a39f2-default-rtdb.firebaseio.com/orders.json",
            method: 'POST',
            body: data,
            headers: {
                'Content-Type': 'application/json',
            },
        }
        sendRequest(requestConfig, (data) => console.log(data));

        if (!error && !isLoading) {
            cartContext.clearItems();
        }
    }

    const openCheckoutHandler = () => {
        setCheckoutVisible(true);
    }

    const closeCheckoutHandler = () => {
        setCheckoutVisible(false);
    }

    return (
      <Modal onClick={props.onClick}>
          <ul className={classes["cart-items"]}>
              {content}
          </ul>
          <div className={classes.total}>
              <span>Total Amount</span>
              <span>{`$${cartContext.items.reduce((curNumber, item) => {
                  return curNumber + item.price * item.amount;
              }, 0).toFixed(2)}`}</span>
          </div>
          <div className={classes.actions}>
              <button className={classes["button--alt"]} onClick={props.onClose}>Close</button>
              {hasItems && <button className={classes.button} onClick={openCheckoutHandler}>Order</button>}
          </div>
          {checkoutVisible && <Checkout onClose={closeCheckoutHandler} onConfirm={confirmCheckoutHandler}/>}
      </Modal>
    );
}

export default Cart;
