import React, {useState} from "react";

const CartItems = React.createContext({
    items: [],
    addItem: () => {},
    removeItem: () => {}});

export const CartItemsProvider = (props) => {
    const [items, setItems] = useState([]);

    const addItemHandler = (item) => {
        setItems((prev) => {
            return [...prev, item];
        });
        console.log(items);
    }

    const removeItemHandler = (item) => {
        setItems((prev) => {
            return prev.splice(prev.indexOf(item), 1);
        });
    }

    return <CartItems.Provider value={{items: items, addItem: addItemHandler, removeItem: removeItemHandler}}>{props.children}</CartItems.Provider>
}

export default CartItems;
